import styled from "styled-components"
import AddIcon from '@material-ui/icons/Add';
import { useEffect, useState } from "react";
import axios from "axios";
// import WineRouge from '../../public/images/home_wine_1_compressed.jpg'

const URL = 'http://localhost:8000/blogs'

const AddProduct = () => {
    return (
        <BackgroundPhoto>
            <Container>
                <WineImage src="https://chamisalvineyards.com/wp-content/uploads/2018/06/CV-morrito-pinot-WP_705x214.png" />
                <Form onSubmit="">
                    <WriteFormGroup>
                        <LabelPlus htmlFor="fileInput">
                            <AddIcon />
                        </LabelPlus>
                        <InputFile type="file" id="fileInput" />
                        <InputTitle
                            type="text"
                            placeholder="Title"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputDate
                            type="date"
                            placeholder="Date"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputPrice
                            type="number"
                            placeholder="Number"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputAlcohol
                            type="number"
                            placeholder="Number"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputTitle
                            type="text"
                            placeholder="Title"
                            className="writeInput"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputAroma
                            type="text"
                            placeholder="Aroma"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputFlavor
                            type="text"
                            placeholder="Flavor"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                        <InputFinish
                            type="text"
                            placeholder="Finish"
                            autoFocus={true}
                        // onChange={e => setTitle(e.target.value)}
                        />
                    </WriteFormGroup>
                    <Button type="submit">Publish</Button>
                </Form>
            </Container>
        </BackgroundPhoto>
    )
}

const BackgroundPhoto = styled.div`
    background-image: url('https://dob3qj4mreqzz.cloudfront.net/lines.png');
    margin-top: 50px;
    margin-bottom: 100px;
`

const Container = styled.div`
    width: 70%;
    height: 90vh;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
`

const WineImage = styled.img`
    width: 200px;
    margin-right: 200px;
    transition: all 0.4s ease;

    &:hover {
        cursor: pointer;
        transform: translateY(-10px);
    }
`

const Form = styled.form`
    height: 100%;
`

const WriteFormGroup = styled.div``
const LabelPlus = styled.label``
const InputFile = styled.input`
    display: none;
    height: 200px;
    width: 200px;
    background-color: aqua;
`

const InputTitle = styled.input``
const InputDate = styled.input``
const InputPrice = styled.input``
const InputAlcohol = styled.input``
const InputAroma = styled.input``
const InputFlavor = styled.input``
const InputFinish = styled.input``
const Button = styled.button``

export default AddProduct
