import { useContext } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components"
import { Context } from "../context/Context";
import '../index.css'
import { ShoppingBasket, ShoppingCartOutlined, MenuOutlined, } from "@material-ui/icons"
// import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
// import Menu from "@material-ui/core/Menu";
// import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";
import React from "react";
import { CancelOutlined } from "@material-ui/icons";
import SwipeableDrawer from '@mui/material/SwipeableDrawer';
import List from '@mui/material/List';
// import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import { Button } from "@material-ui/core";
import { Lines } from "./Lines";
import { Facebook } from "@material-ui/icons";
import { Instagram } from "@material-ui/icons";

const useStyles = makeStyles({
    menuItem: {
        background: "#A36833",
    },
});

export default function NavBar() {
    const [state, setState] = React.useState({
        right: false,
    });

    const toggleDrawer = (anchor, open) => (event) => {
        if (
            event &&
            event.type === 'keydown' &&
            (event.key === 'Tab' || event.key === 'Shift')
        ) {

            return;
        }

        setState({ ...state, [anchor]: open });
    };

    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    }

    const handleClose = () => {
        setAnchorEl(null);
    };
    const shopFunc = () => {
        window.location.href = "/shop"
    }
    const homeFunc = () => {
        window.location.href = "/"
    }
    const FbFunc = () => {
        window.location.href = "https://www.facebook.com/Chamisal.Vineyards"
    }
    const InstaFunc = () => {
        window.location.href = "https://www.instagram.com/chamisalvyd/"
    }
    return (

        <Container>
            <Wrapper>
                <Left>
                    <ShoppingCartOutlined onClick={shopFunc} />
                </Left>
                <Center>
                    <Logo onClick={homeFunc}>CHAMISAL</Logo>
                    <LogoSmall>VINEYARDS</LogoSmall>
                </Center>
                <Right>
                    {['right'].map((anchor) => (
                        <React.Fragment key={anchor}>
                            <MenuOutlined onClick={toggleDrawer(anchor, true)} />
                            <SwipeableDrawer
                                anchor={anchor}
                                open={state[anchor]}
                                onClose={toggleDrawer(anchor, false)}
                                onOpen={toggleDrawer(anchor, true)}
                            >
                                <List style={{ position: "relative" }}>
                                    <Lines />
                                    <Toggle>
                                        <Button ><ShoppingCartOutlined onClick={shopFunc} style={ToggleStyles} /></Button>
                                        <CancelOutlined style={ToggleStyles} onClick={toggleDrawer(anchor, false)} />
                                    </Toggle>
                                    <ListItem onClick={handleClose} className="listItems">
                                        {" "}
                                        <a href="/">HOME</a>
                                    </ListItem>
                                    <ListItem onClick={handleClose} className="listItems">
                                        <a href="/shop">SHOP</a>
                                    </ListItem>
                                    <ListItem onClick={handleClose} className="listItems">
                                        <a href="/login">LOGIN</a>
                                    </ListItem>
                                    <ListItem onClick={handleClose} className="listItems">
                                        <a href="/registration">REGISTRATION</a>
                                    </ListItem>
                                    <ListItem onClick={handleClose} className="listItems">
                                        <a href="/add">ADD</a>
                                    </ListItem>
                                    <ListItem onClick={handleClose} className="listItems">
                                        <a href="/basket">BASKET</a>
                                    </ListItem>
                                    <ListItem onClick={handleClose} className="listItems">
                                        <a href="/about">ABOUT</a>
                                    </ListItem>
                                    <Social>
                                        <Facebook onClick={FbFunc} style={SocialIcons}></Facebook>
                                        <Instagram onClick={InstaFunc} style={SocialIcons} />
                                    </Social>
                                </List>
                            </SwipeableDrawer>
                        </React.Fragment>
                    ))}
                </Right>
            </Wrapper>
        </Container>
    )
}

const ToggleStyles = {
    cursor: "pointer",
    borderRadius: "50%",
    border: "2px solid #d4d0c3",
    color: "#d4d0c3",
    zIndex: "10",
    width: "35px",
    height: "35px",
    padding: "5px"
};

const SocialIcons = {
    cursor: "pointer",
    color: "#d4d0c3",
    opacity: "1",
    borderRadius: "50%",
    border: "2px solid #d4d0c3",
    width: "35px",
    height: "35px",
    zIndex: "10",
    padding: "5px",
    marginRight: "30px"
}

const Container = styled.div`
`

const Wrapper = styled.div`
  padding: 10px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const Left = styled.div`
  align-items: center;
  cursor: pointer;
  border-radius: 50%;
  border: 2px solid black;
  padding: 10px;
  display: flex;
  align-content: center;
  justify-content: center;
`;

const Center = styled.div`
  flex: 1;
  text-align: center;
`;
const Logo = styled.h1`
  font-family: "source_sans_proregular", sans-serif;
  color: #404040;
  font-weight: bold;
  letter-spacing: 0.2em;
  cursor: pointer;
`;
const LogoSmall = styled.h3`
  color: #808080;
  font-size: small;
  letter-spacing: 0.3em;
`;
const Right = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  max-width: 100%;
`;
const Toggle = styled.div`
display:flex;
align-items: center;
justify-content: space-between;
margin: 5px;
`;
const Social = styled.div`
display:flex;
align-items: center;
justify-content: center;
margin-top: 50px;
`

const Shop = styled.div`
`
