import { ArrowBackSharp } from '@material-ui/icons'
import { Link } from 'react-router-dom'
import styled from 'styled-components'
import Button from './Button'
import '../index.css'
import axios from 'axios'
import { useEffect, useState } from 'react'
import { useParams } from "react-router-dom";
import { useHistory, useLocation } from "react-router";
import useFetch from '../tools/useEffect'
// import useFetch from '../../../../../../Volumes/NO NAME/React/myreact/src/useFetch'

const URL = "http://localhost:8000/wines/"

const Product = () => {

    const [data, setData] = useState([])
    const { id } = useParams()
    const { data: blog, isPending, error } = useFetch(URL + id)
    const history = useHistory()

    const handleClick = () => {
        fetch(URL + id, {
            method: 'POST'
        }).then(() => {
            console.log('this item is send it')
            history.push('/')
        })
    }
    // useEffect(() => {
    //     fetchData()
    // }, [])

    // const fetchData = async () => {
    //     try {
    //         const res = await axios.get(`${URL}/:${id}`)
    //         setData(res.data)
    //         console.log(res.data);
    //     } catch (error) {
    //         console.log('Wrong data')
    //         // history.push('/')
    //     }
    // }

    // const location = useLocation()
    // const path = location.pathname.split('/')

    return (
        <ProductWrapper>
            <Link to="/shop">
                <ArrowBackSharp className="arrow" />
            </Link>
            {data.map(w => (
                <Container>
                    <Image src={w.pic} />
                    <Wrapper key=''>
                        <Date>2019</Date>
                        <Title>{console.log(data)}</Title>
                        <Price>100€</Price>
                        <Actions>
                            <Select>
                                <Option value="1">1</Option>
                                <Option value="2">2</Option>
                                <Option value="3">3</Option>
                                <Option value="4">4</Option>
                                <Option value="5">5</Option>
                                <Option value="6">6</Option>
                                <Option value="7">7</Option>
                                <Option value="8">8</Option>
                                <Option value="9">9</Option>
                                <Option value="10">10</Option>
                                <Option value="11">11</Option>
                                <Option value="12">12</Option>
                            </Select>
                            <Button className="actionButton" value="PURCHASE"></Button>
                            <Button className="actionButton" value="DELETE"></Button>
                            <Button className="actionButton" value="EDIT"></Button>
                        </Actions>
                        <SmallTitle>Aromas</SmallTitle>
                        <SmallDesc>Classically varietal. Intensely aromatic. Baked granny smith apple, citrus crème brulee</SmallDesc>
                        <SmallTitle>Flavors</SmallTitle>
                        <SmallDesc>Opulent oak toast notes. Defined acidity, hints of vanilla and butterscotch</SmallDesc>
                        <SmallTitle>Finish</SmallTitle>
                        <SmallDesc>Focused and lengthy</SmallDesc>
                        <SmallTitle>Alcohol %</SmallTitle>
                        <SmallDesc>13.8</SmallDesc>
                        <SmallTitle>Size</SmallTitle>
                        <SmallDesc>750 ml</SmallDesc>
                    </Wrapper>
                </Container>
            ))}
        </ProductWrapper>
    )
}

const ProductWrapper = styled.div`
    background-image: url("https://dob3qj4mreqzz.cloudfront.net/lines.png");
    margin-top: 50px;
    margin-bottom: 100px;
`

const Container = styled.div`
    width: 70%;
    height: 90vh;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
`

const Image = styled.img`
    width: 200px;
    margin-right: 200px;
    transition: all 0.4s ease;

    &:hover {
        cursor: pointer;
        transform: translateY(-10px);
    }
`

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
`

const Date = styled.span`
    color: var(--color-date);
    font-size: 30px;
    font-weight: 500;
    letter-spacing: 2px;
    font-family: 'sans-serif';
`

const Title = styled.h1`
    margin-top: 20px;
    margin-bottom: 20px;
    color: var(--color-title);
    font-size: 60px;
    letter-spacing: 2px;
    font-family: 'Requiem';
    font-weight: 100;
    text-transform: uppercase;
`

const Price = styled.span`
    color: var(--color-title);
    font-weight: 400;
    opacity: 0.9;
    letter-spacing: 1px;
    font-size: 35px;
    font-family: 'sans-serif';
`

const Actions = styled.div`
    display: flex;
    align-items: center;
    /* justify-content: space-between; */
    margin: 20px 0 30px 0;
`

const Select = styled.select`
    border: 1px solid rgba(0, 0, 0, 0.2);
    padding: 12px;
`

const Option = styled.option`
    font-family: 'sans-serif';
`

const SmallTitle = styled.h3`
    color: var(--color-title);
    font-weight: 500;
    opacity: 0.8;
    letter-spacing: 1px;
    margin-bottom: 10px;
`

const SmallDesc = styled.span`
    display: block;
    color: var(--color-title);
    opacity: 0.7;
    margin-bottom: 10px;
`

export default Product
