import styled from 'styled-components'
import { Link } from "react-router-dom";

const Registration = () => {
    return (
        <BackImage>
            <Container>
                <Infos>
                    <Title>create a new account</Title>
                    <Desc>Fill in the form below to create a new account.</Desc>
                </Infos>
                <PersonInfo>personal information</PersonInfo>
                <Form onSubmit="">
                    <FirstGroup>
                        <InputInfo>
                            {/* <Label>*username</Label> */}
                            <Input type="text" placeholder="First Name" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="text" placeholder="Last Name" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="text" placeholder="Address" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="text" placeholder="City" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Select>
                                <Option>Greece</Option>
                                <Option>Italy</Option>
                                <Option>Spain</Option>
                            </Select>
                        </InputInfo>
                    </FirstGroup>
                    <SecondGroup>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="number" placeholder="Zip Code" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="number" placeholder="Phone" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="number" placeholder="Mobile Phone" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="email" placeholder="Email" />
                        </InputInfo>
                        <InputInfo>
                            {/* <Label>*password</Label> */}
                            <Input type="date" placeholder="Email" />
                        </InputInfo>
                    </SecondGroup>

                    {/* <Label className="success error">*password</Label>
                    <Input type="password" />
                    <Label className="success error">*password</Label>
                    <Input type="password" />
                    <Label className="success error">*password</Label>
                    <Input type="password" /> */}
                    <Button type="submit" onClick="">log in</Button>
                </Form>
            </Container>
        </BackImage>
    )
}

const BackImage = styled.div`
    background-image: url("https://dob3qj4mreqzz.cloudfront.net/lines.png");
    margin-top: 150px;
    margin-bottom: 150px;
`

const Container = styled.div`
    width: 70%;
    max-width: 100%;
    margin: 0 auto;
    display: flex;
    /* align-items: center; */
    /* justify-content: center; */
    flex-direction: column;
    /* text-align: center; */
`

const Infos = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    text-align: center;
`

const Title = styled.h1`
    color: var(--color-title);
    font-size: 45px;
    font-weight: 500;
    font-family: 'Requiem';
    letter-spacing: 2px;
    text-transform: uppercase;
    margin-bottom: 20px;
`

const Desc = styled.p`
    color: var(--color-para);
    font-size: 14px;
    font-weight: 500;
    font-family: 'Brandon Grotesque';
    width: 50%;
    margin: 0 auto;
    letter-spacing: 4px;
    text-transform: uppercase;
    margin-bottom: 20px;
    line-height: 20px;
`

const PersonInfo = styled.span`
    color: var(--color-title);
    font-size: 25px;
    font-weight: 100;
    font-family: 'Brandon Grotesque';
    letter-spacing: 2px;
    text-transform: uppercase;
    text-align: left;
    border-bottom: 1px solid rgba(0, 0 ,0, 0.4);
    margin-top: 50px;
    padding: 10px;
`

const Form = styled.form`
    display: flex;
    justify-content: space-evenly;
    padding-top: 100px;
    padding-bottom: 100px;
    width: 100%;
    position: relative;
`

const FirstGroup = styled.div`
    width: 40%;
`
const SecondGroup = styled.div``

const InputInfo = styled.div`
`

const Select = styled.select``
const Option = styled.option``


// const Label = styled.label`
//     margin-bottom: 20px;
//     color: var(--color-title);
//     font-size: 15px;
//     font-weight: 500;
//     letter-spacing: 2px;
//     opacity: 0.7;
//     text-transform: uppercase;
// `

const Input = styled.input`
    height: 30px;
    outline: none;
    border: none;
    border-bottom: 3px solid var(--color-gray);
    font-family: 'Brandon Grotesque';
    font-size: 17px;
    letter-spacing: 1.2px;
    margin: 20px;
    width: 100%;
`

const Button = styled.button`
    width: 40%;
    margin-top: 50px;
    border: 3px solid var(--color-brown);
    background-color: #fff;
    border-radius: 25px;
    padding: 12px;
    font-size: 15px;
    letter-spacing: 2.5px;
    text-transform: uppercase;
    font-weight: 600;
    color: var(--color-para);
    align-self: center;
    position: absolute;
    bottom: 0;

    &:hover{
        cursor: pointer;
        color: #fff;
        background-color: var(--color-brown);
    }
`

export default Registration
