import styled from "styled-components"
import Modal from 'react-modal';
import ShoppingCart from './ShoppingCart'
import { useState } from "react";


const ShoppingModal = () => {

    const customStyles = {
        content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: '#F0AA89'
        }
    }

    return (
        <>
            <button onClick={setModalIsOpenToTrue}>Click to View My Favourite Animes</button>

            <Modal style={customStyles} className="modal" isOpen={modalIsOpen} onRequestClose>
                <ButtonClose onClick={setModalIsOpenToFalse}>x</ButtonClose>
                <ShoppingCart />
            </Modal>
        </>
    )
}

const ButtonClose = styled.button`
    color: var(--color-para);
    border-radius: 30px;
    padding-right: 20px;
    padding-left: 20px;
    height: 35px;
    font-size: 12px;
    font-weight: 700;
    letter-spacing: 0.7px;
    border: 3px solid var(--color-brown);
    background-color: #fff;
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;

    &:hover{
    background-color: var(--color-brown);
    color: #fff;
    transition: all 0.2s ease-in-out;
}
`

export default ShoppingModal
