import {
  ShoppingBasket,
  ShoppingCartOutlined,
  MenuOutlined,
} from "@material-ui/icons";
import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import "../index.css";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import { makeStyles } from "@material-ui/core/styles";
import { CancelOutlined } from "@material-ui/icons";
import SwipeableDrawer from '@mui/material/SwipeableDrawer';
import List from '@mui/material/List';
import Divider from '@mui/material/Divider';
import ListItem from '@mui/material/ListItem';
import { Button } from "@material-ui/core";
import { Lines } from "./Lines";
import { Facebook } from "@material-ui/icons";
import { Instagram } from "@material-ui/icons";

const useStyles = makeStyles({
  menuItem: {
    background: "#A36833",
  },
});

export default function NavBar() {
  const [state, setState] = React.useState({
    right: false,
  });

  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event &&
      event.type === 'keydown' &&
      (event.key === 'Tab' || event.key === 'Shift')
    ) {

      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };


  const handleClose = () => {
    setAnchorEl(null);
  }
  const FbFunc = () => {
    window.location.href = "https://www.facebook.com/Chamisal.Vineyards"
  }
  const InstaFunc = () => {
    window.location.href = "https://www.instagram.com/chamisalvyd/"
  }
  return (

    <Container>
      <Wrapper>
        <Left>
          <Link to='/shop'>
            <ShoppingCartOutlined />
          </Link>
        </Left>
        <Center>
          <Link to='/'>
            <Logo>CHAMISAL</Logo>
            <LogoSmall>VINEYARDS</LogoSmall>
          </Link>
        </Center>
        <Right>
          {['right'].map((anchor) => (
            <React.Fragment key={anchor}>
              <MenuOutlined onClick={toggleDrawer(anchor, true)} />
              <SwipeableDrawer
                anchor={anchor}
                open={state[anchor]}
                onClose={toggleDrawer(anchor, false)}
                onOpen={toggleDrawer(anchor, true)}
              >
                <List style={{ position: "relative" }}>
                  <Lines />
                  <Toggle>
                    <Button>
                      <ShoppingCartOutlined onClick={shopFunc} style={ToggleStyles} /></Button>
                    <CancelOutlined style={ToggleStyles} onClick={toggleDrawer(anchor, false)} />
                  </Toggle>
                  <ListItem onClick={handleClose} className="listItems">
                    <a href="/">HOME</a>
                  </ListItem>
                  <ListItem onClick={handleClose} className="listItems">
                    <a href="/shop">SHOP</a>
                  </ListItem>
                  <ListItem onClick={handleClose} className="listItems">
                    <a href="/login">LOGIN</a>
                  </ListItem>
                  <ListItem onClick={handleClose} className="listItems">
                    <a href="/registration">REGISTRATION</a>
                  </ListItem>
                  <Social>
                    <Link>
                      <Facebook onClick={FbFunc} style={SocialIcons} />
                    </Link>
                    <Instagram onClick={InstaFunc} style={SocialIcons} />
                  </Social>
                </List>
              </SwipeableDrawer>
            </React.Fragment>
          ))}
        </Right>
      </Wrapper>
    </Container>
  );
}
