const ToggleStyles = {
  cursor: "pointer",
  borderRadius: "50%",
  border: "2px solid #d4d0c3",
  color: "#d4d0c3",
  zIndex: "10",
  width: "35px",
  height: "35px",
  padding: "5px"
};

const SocialIcons = {
  cursor: "pointer",
  color: "#d4d0c3",
  opacity: "1",
  borderRadius: "50%",
  border: "2px solid #d4d0c3",
  width: "35px",
  height: "35px",
  zIndex: "10",
  padding: "5px",
  marginRight: "30px"
}

const Container = styled.div`
  /* height: 70px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1); */
`;

const Wrapper = styled.div`
  padding: 10px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Left = styled.div`
  align-items: center;
  cursor: pointer;
  border-radius: 50%;
  border: 2px solid black;
  padding: 10px;
  display: flex;
  align-content: center;
  justify-content: center;
`;

const Shop = styled.div``;

const Center = styled.div`
  flex: 1;
  text-align: center;
`;
const Logo = styled.h1`
  font-family: "source_sans_proregular", sans-serif;
  color: #404040;
  font-weight: bold;
  letter-spacing: 0.2em;
  cursor: pointer;
`;
const LogoSmall = styled.h3`
  color: #808080;
  font-size: small;
  letter-spacing: 0.3em;
`;
const Right = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  max-width: 100%;
`;
const Toggle = styled.div`
display:flex;
align-items: center;
justify-content: space-between;
margin: 5px;
`;
const Social = styled.div`
display:flex;
align-items: center;
justify-content: center;
margin-top: 50px;
`;
