import styled from "styled-components"
import { Table, TableBody, TableCell, TableHead, TableRow, TableSortLabel } from '@material-ui/core'
import StripeContainer from "./StripeContainer"
import { Link } from 'react-router-dom'
import { useState } from "react"
import Modal from 'react-modal';
import Button from './Button';
import '../index.css'

const ShoppingCart = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false)

    const setModalIsOpenToTrue = () => {
        setModalIsOpen(true)
    }

    const setModalIsOpenToFalse = () => {
        setModalIsOpen(false)
    }


    return (
        <Container>
            <WrapperTitle>
                <TitleCart>Shopping Cart</TitleCart>
            </WrapperTitle>
            <WrapperTable>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell><TableSortLabel>Your Items</TableSortLabel></TableCell>
                            <TableCell><TableSortLabel>PRICE</TableSortLabel></TableCell>
                            <TableCell><TableSortLabel>Quantity</TableSortLabel></TableCell>
                            <TableCell><TableSortLabel>Total</TableSortLabel></TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        <TableRow>
                            <TableCell>
                                <Infos>
                                    <Img src="https://chamisalvineyards.com/wp-content/uploads/2018/06/CV-morrito-pinot-WP_705x214.png" alt="" />
                                    <InfosItems>
                                        <Date>2018</Date>
                                        <Title>HELLazdazdzadO</Title>
                                        <ButtonRemove>Remove</ButtonRemove>
                                    </InfosItems>
                                </Infos>
                            </TableCell>
                            <TableCell>100€</TableCell>
                            <TableCell>
                                <InfoQuantity>
                                    <Input type="text" pattern="[0-9]*"></Input>
                                    <UpdateButton>Update</UpdateButton>
                                </InfoQuantity>
                            </TableCell>
                            <TableCell>€100</TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </WrapperTable>
            <WrapperCart>
                <Payment src="https://i.ibb.co/Qfvn4z6/payment.png" />
                <Button onClick={setModalIsOpenToTrue}></Button>
                <ModalContainer>
                    <Modal style={customStyles} isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)}>
                        <ButtonClose onClick={setModalIsOpenToFalse}>x</ButtonClose>
                        <StripeContainer />
                    </Modal>
                </ModalContainer>
            </WrapperCart>
        </Container>
    )
}

const Payment = styled.img``

const ModalContainer = styled.div`
    position: absolute;
`

const customStyles = {
    content: {
        width: '50%',
        height: '30vh',
        backgroundColor: '#fff',
        border: 'none',
        borderRadius: '5px',
        boxShadow: '0 0 10px rgba(0, 0, 0, 0.5)'
    }
}

const Container = styled.div`
    width: 70%;
    margin: 100px auto;
`
const ButtonClose = styled.button`
    position: absolute;
    top: 5px;
    left: 10px;
    font-weight: 400;
    font-size: 20px;
    border: none;
    cursor: pointer;
    background-color: #fff;
`
const Img = styled.img`
    width: 25px;
`
const WrapperTitle = styled.div``

const TitleCart = styled.h1`
    margin-bottom: 20px;
    color: #404040;
    font-family: Crimson Text;
    font-weight: 100;
    font-size: 40px;
`

const WrapperTable = styled.div``

const WrapperCart = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 20px;
`

const Infos = styled.div`
    display: flex;
    align-items: center;
`

const InfosItems = styled.div`
    margin-left: 10px;
`

const Date = styled.span`
    font-size: 16px;
    margin-right: 10px;
    color: #000;
`

const Title = styled.span`
    font-size: 16px;
    color: #000;
`

const Input = styled.input`
    width: 60px;
    height: 35px;
    text-align: center;
    outline: none;
`

const InfoQuantity = styled.div`
`
const ButtonRemove = styled.button`
    display: block;
    border: none;
    background-color: #fff;
    border-bottom: 1px solid #000;
    cursor: pointer;
`

const UpdateButton = styled.button`
    display: block;
    border: none;
    background-color: #fff;
    border-bottom: 1px solid #000;
    cursor: pointer;
    margin-top: 5px;
    position: relative;
    left: 7px;
`

// const Paymentbutton = styled.button`

// `

export default ShoppingCart
