import { ArrowBackSharp, Height } from '@material-ui/icons'
import { Link } from 'react-router-dom'
import styled from 'styled-components'
import Button from './Button'
import '../index.css'
import axios from 'axios'
import { useEffect, useState } from 'react'
import { useParams } from "react-router-dom";
import { useHistory, useLocation } from "react-router";
import useFetch from '../tools/useEffect'
import NotFound from './NotFound'
import ShoppingCart from './ShoppingCart'
import Modal from 'react-modal';

const URL = "http://localhost:8000/wines/"

const Product = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false)

    const setModalIsOpenToTrue = () => {
        setModalIsOpen(true)
    }
    const setModalIsOpenToFalse = () => {
        setModalIsOpen(false)
    }

    console.log(modalIsOpen)


    const location = useLocation()
    const id = location.state?.wineId

    const [wineData, setWineData] = useState([])

    const fetchData = async () => {
        try {
            const res = await axios.get(URL)
            setWineData(res.data)
        } catch (error) {
            console.log('Wrong data')
        }
    }

    useEffect(() => {
        fetchData()
    }, [])

    const product = wineData.find(p => p.id == id);

    return (
        <ProductWrapper>
            <Link to="/shop">
                <ArrowBackSharp className="arrow" />
            </Link>
            {product ? (
                <Container>
                    <Image src={product.pic} />
                    <Wrapper key={product.id}>
                        <Date>{product.date}</Date>
                        <Title>{product.title}</Title>
                        <Price>{product.price}</Price>
                        <Actions>
                            <Select>
                                <Option value="1">1</Option>
                                <Option value="2">2</Option>
                                <Option value="3">3</Option>
                                <Option value="4">4</Option>
                                <Option value="5">5</Option>
                                <Option value="6">6</Option>
                                <Option value="7">7</Option>
                                <Option value="8">8</Option>
                                <Option value="9">9</Option>
                                <Option value="10">10</Option>
                                <Option value="11">11</Option>
                                <Option value="12">12</Option>
                            </Select>
                            <Button className="actionButton" value="PURCHASE"></Button>
                            <Button className="actionButton" value="DELETE"></Button>
                            <Button className="actionButton" value="EDIT"></Button>
                            <button onClick={setModalIsOpenToTrue}>Click to View My Favourite Animes</button>
                        </Actions>
                        <SmallTitle>Aromas</SmallTitle>
                        <SmallDesc>{product.aroma}</SmallDesc>
                        <SmallTitle>Flavors</SmallTitle>
                        <SmallDesc>{product.flavor}</SmallDesc>
                        <SmallTitle>Finish</SmallTitle>
                        <SmallDesc>{product.finish}</SmallDesc>
                        <SmallTitle>Alcohol %</SmallTitle>
                        <SmallDesc>{product.alcohol}</SmallDesc>
                        <SmallTitle>Size</SmallTitle>
                        <SmallDesc>750 ml</SmallDesc>
                        <Modal isOpen={modalIsOpen}>
                            <ButtonClose onClick={setModalIsOpenToFalse}>x</ButtonClose>
                            <ShoppingCart />
                        </Modal>
                    </Wrapper>
                </Container>
            ) : (
                <ProductNotFound>
                    <NotFound />
                </ProductNotFound>
            )}
        </ProductWrapper>
    )
}

const ProductWrapper = styled.div`
    background-image: url("https://dob3qj4mreqzz.cloudfront.net/lines.png");
    margin-top: 50px;
    margin-bottom: 100px;
`

const Container = styled.div`
    width: 70%;
    height: 90vh;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
`

const Image = styled.img`
    width: 200px;
    margin-right: 200px;
    transition: all 0.4s ease;

    &:hover {
        cursor: pointer;
        transform: translateY(-10px);
    }
`

const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
`

const Date = styled.span`
    color: var(--color-date);
    font-size: 30px;
    font-weight: 500;
    letter-spacing: 2px;
    font-family: 'sans-serif';
`

const Title = styled.h1`
    margin-top: 20px;
    margin-bottom: 20px;
    color: var(--color-title);
    font-size: 60px;
    letter-spacing: 2px;
    font-family: 'Requiem';
    font-weight: 100;
    text-transform: uppercase;
`

const Price = styled.span`
    color: var(--color-title);
    font-weight: 400;
    opacity: 0.9;
    letter-spacing: 1px;
    font-size: 35px;
    font-family: 'sans-serif';
`

const Actions = styled.div`
    display: flex;
    align-items: center;
    margin: 20px 0 30px 0;
`

const Select = styled.select`
    border: 1px solid rgba(0, 0, 0, 0.2);
    padding: 12px;
`

const Option = styled.option`
    font-family: 'sans-serif';
`

const SmallTitle = styled.h3`
    color: var(--color-title);
    font-weight: 500;
    opacity: 0.8;
    letter-spacing: 1px;
    margin-bottom: 10px;
`

const SmallDesc = styled.span`
    display: block;
    color: var(--color-title);
    opacity: 0.7;
    margin-bottom: 10px;
`

const ProductNotFound = styled.div`
    height: 50vh;
    display: flex;
    justify-content: center;
    align-items: center;
`

const ButtonClose = styled.button`
    color: var(--color-para);
    border-radius: 30px;
    padding-right: 20px;
    padding-left: 20px;
    height: 35px;
    font-size: 12px;
    font-weight: 700;
    letter-spacing: 0.7px;
    border: 3px solid var(--color-brown);
    background-color: #fff;
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;

    &:hover{
    background-color: var(--color-brown);
    color: #fff;
    transition: all 0.2s ease-in-out;
    }
`



export default Product
